import React, { FC, useEffect } from "react";
import { Link, useLocation, useMatch, useResolvedPath } from "react-router-dom";
import styled, { css } from "styled-components";
import { Tooltip } from "antd";

const NavItem = ({ to, name, open }) => {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname });

  const matchTable = {
    Home: <HomeIcon />,
    Customers: <CustomersIcon />,
    Cashires: <CashiersIcon />,
    Branches: <BranchesIcon />,
    Offers: <OffersIcon />,
    Campaignes: <CampaignesIcon />,
    Partnerships: <PartnershipsIcon />,
    Settings: <SettingsIcon />,
  };

  return (
    <Container active={isActive ? true : false} as={Link} to={to} open={open}>
      {open ? (
        <NavIcon
          viewBox="0 0 23 23"
          fill={isActive ? "#fff" : "#808191"}
          xmlns="http://www.w3.org/2000/svg"
        >
          {matchTable[name]}
        </NavIcon>
      ) : (
        <Tooltip placement="right" title={name} className="tooltip">
          <NavIcon
            viewBox="0 0 23 23"
            fill={isActive ? "#fff" : "#808191"}
            xmlns="http://www.w3.org/2000/svg"
          >
            {matchTable[name]}
          </NavIcon>
        </Tooltip>
      )}

      <NavLabel open={open ? true : false} active={isActive ? true : false}>
        {name}
      </NavLabel>
    </Container>
  );
};

export default NavItem;

const Container = styled.li`
  margin: 0 1rem;
  height: 50px;

  /* border-left: ${(p) => (p.active ? "1px solid #00BEA7;" : "none")}; */
  /* border-radius: 15px; */
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 19px;
  transition: all 0.3s ease-in-out;
  ${({ active }) =>
    active
      ? css`
          background: #282a37;
          border-radius: 15px;
          color: #ffffff;
          font-family: "GilroyExtraBold";
          font-size: 18px;
          line-height: 32px;
        `
      : css`
          border-left: none;
          border-radius: 0px;
        `}
  ${({ open }) =>
    open
      ? css`
          padding-left: 1rem;
        `
      : css`
          padding-left: 1rem;
          /* justify-content: center; */
        `}
`;
const NavIcon = styled.svg`
  width: 23px;
  height: 23px;
`;
const NavLabel = styled.div`
  font-size: 16px;
  ${({ open }) => (open ? `display: block;` : `display: none;`)}
  color: ${(p) => (p.active ? "#ffffff" : "#808191")};
  padding: 0;
  font-family: "GilroyExtraBold";
`;

